import sys
import os
import argparse



def fastq_seqs(fname):
    i = 0
    with open(fname, 'r') as infile:
        for line in infile:
            if i%4 == 1:
                yield line
                i = 1
            i += 1

def condense_dict_rc(my_dict):
    '''
    because we don't know which reads are on the same strand, we can combine kmer couns that are reverse complements of each other
    :param my_dict: kmer count dict
    :return: dict
    '''
    def rc(dna):
        MAPPING = {"A": "T", "T": "A", "C": "G", "G": "C"}
        return ''.join([MAPPING[x] for x in dna][::-1])

    new_dict = {}
    for k in sorted(my_dict.keys()):
        if k not in new_dict and rc(k) not in new_dict:
            new_dict[k] = my_dict[k]

        elif k not in new_dict and rc(k) in new_dict:
            new_dict[rc(k)] += my_dict[k]

        else:
            print(k)  # This should never happen

    # Because some kmers are their own reverse complement (palendromes) we should double their counts
    for k in new_dict.keys():
        if k == rc(k):
            new_dict[k] = new_dict[k]*2

    return new_dict






def count_file(fname, **kwargs):
    """
    For a file, we want to know:
    GC_content
    CpG_count (and GpC, because we don't know which strand we're on)((basically count 2mers))
    may as well do 3mers and 4mers too

    :param fname: string, name of fastqfile
    :param kwargs: nothing yet
    :return:
    """

    twomers = {}
    threemers = {}
    sixmers = {}

    gc_count = 0
    total_bases = 0
    for seq in fastq_seqs('fake.fastq'):
        seq = seq.replace('\n', '')
        last_6 = []
        for char in seq:
            total_bases += 1
            last_6.append(char)
            if len(last_6) > 6:
                last_6.pop(0)
            if char == "C" or char == "G":
                gc_count += 1

            #This next part looks janky, and could be better, but it works and has decent time complexity
            if len(last_6) == 6:
                #count 2mers
                twomer = ''.join(last_6[:2])
                twomers[twomer] = twomers.get(twomer, 0) + 1

                # count 3mers
                threemer = ''.join(last_6[:3])
                threemers[threemer] = threemers.get(threemer, 0) + 1

                # count 4mers
                sixmer = ''.join(last_6[:6])
                sixmers[sixmer] = sixmers.get(sixmer, 0) + 1

            elif len(last_6) == 3:
                # count 2mers
                twomer = ''.join(last_6[:2])
                twomers[twomer] = twomers.get(twomer, 0) + 1

                # count 3mers
                threemer = ''.join(last_6[:3])
                threemers[threemer] = threemers.get(threemer, 0) + 1

            elif len(last_6) == 2:
                # count 2mers
                twomer = ''.join(last_6[:2])
                twomers[twomer] = twomers.get(twomer, 0) + 1

            else:
                continue
    return gc_count, total_bases, condense_dict_rc(twomers), condense_dict_rc(threemers), condense_dict_rc(sixmers)





